/** 
pragma solidity >=0.5.16 <0.8.0;

contract Regulation {


    struct donor_account {
        int id;
        address addr;
        uint balance;

    }

    struct involve {
        address addr;
        uint amount;
    }

    donor_account[] donors;
    involve[] involved;
    address payable organization;
    address[] alldonors;
    int donorCounter;
    bool susTX;
    uint maxBalance;
    uint threshold;



    constructor() {
        donorCounter = 0;
        maxBalance = 50 ether;
        susTX = false;
    }

    /**
     * Events

     event warning(string);
     event allSuspects(address, uint);
     event suspecteddonors(address);

    /**
     * Modifier
     *

    modifier onlyorganization() {
        require(msg.sender == organization, "Only organization can call!");
        _;
    }

    modifier onlydonors() {
        bool isdonor = false;
        for(uint i = 0; i < donors.length; ++i) {
            if(donors[i].addr == msg.sender) {
                isdonor = true;
                break;
            }
        }
        require(isdonor, "Only donors can call!");
        _;
    }

    /**
     * Utilities
     *

    /**
     * Contract Functions (only can be access organization)
     *

    function setorganization(address _organizationAddress) public returns (string memory) {

        address _organizationAddress = msg.sender; 
        address payable organization = address(uint160(_organizationsAddress));



        return "";
    }

    function setThreshold (uint _amount) public onlyorganization returns(string memory){
        threshold = _amount * 1 ether;
        return "";
    }

    function getAlert () onlyorganization public {
        if(susTX == true) {
            emit warning("Alert: Huge amount transaction !");
            for(uint i = 0; i < involved.length; ++i) {
                emit allSuspects(involved[i].addr, involved[i].amount);
            }
        } else {
            emit warning("Message: No suspicious transaction !");
        }
        if(address(this).balance > maxBalance) {
            emit warning("Alert: Suspected crypto laundering !");
            for(uint i = 0; i < involved.length; ++i) {
                emit suspecteddonors(alldonors[i]);
            }
        } else {
            emit warning("Message: No suspicious laundering !");
        }
    }

    /**
     * Contract Functions
     *

    function joinAsdonor() public payable returns (string memory) {
        donors.push(donor_account(donorCounter++, msg.sender, address(msg.sender).balance));
        return "";
    }

    function deposit() public payable onlydonors {



        payable(address(this)).transfer(msg.value);
        alldonors.push(msg.sender);
        if(msg.value > threshold) {
            involved.push(involve(msg.sender, msg.value));
            susTX = true;
        }
    }

    function withdraw(uint _amount) public payable onlydonors {
        msg.sender.transfer(_amount * 1 ether);
        alldonors.push(msg.sender);
        if(_amount > threshold) {
            involved.push(involve(msg.sender, _amount));
            susTX = true;
        }
    }

    function getContractBalance() public view returns(uint) {
        return address(this).balance;
    }

    /**
     * Fallback function for the contract to receive ether
     *

    receive () external payable {}
}

*/

pragma solidity >=0.5.16 <0.8.0;

contract LaundererDetector {
    address[] private empty;
    address[] private Donors;
    address[] private hugeDonors;
    uint[] private amountHuge;
    uint private threshold = 10 * 10**18;
    uint private maxSave = 50 * 10**18;
    uint8 private ownerCount;

    event LogDepositMade(address indexed accountAddress, uint amount);
    event LogWithdrawMade(address indexed accountAddress, uint amount);
    mapping (address => uint256) public accBalance;

    address NGO;

    modifier onlyNGO(){
        require(msg.sender == NGO);
        _;

    }

    constructor() public payable {
        NGO = msg.sender;
        ownerCount = 0;
        // accBalance = address(this).balance;

    }

    function withdraw(uint256 amount) public {       
        require(address(this).balance >= amount* 1 ether, "Insufficient Balance");
        msg.sender.transfer(amount * 1 ether);
        Donors.push(msg.sender);
        accBalance[msg.sender] -= amount* 1 ether;

        emit LogWithdrawMade(msg.sender, amount* 1 ether);
    }

    function deposit() public payable {
        // require(msg.value == amount);
        Donors.push(msg.sender);

        accBalance[msg.sender] += msg.value;

        if(msg.value > threshold){
            hugeDonors.push(msg.sender);
            amountHuge.push(msg.value);
        }

        emit LogDepositMade(msg.sender, msg.value);
    }

    function getBalance() public view returns (uint256) {
        return address(this).balance;
        // return accBalance[msg.sender];
    }

    function getAccountAmount() public view returns (uint256) {
        // return address(this).balance;
        return accBalance[msg.sender];
    }

    function getThreshold() public onlyNGO view returns (uint) {
        return threshold / (10**18);
    }

    function setThreshold(uint256 newthreshold) public onlyNGO {
        threshold = newthreshold * 10**18;
    }

    // function potentialLaunderers() public  {
    //     if(address(this).balance > maxSave){
    //         for (uint i=0; i < 5; i += 1) {
    //             potential_launderers[i] = Donors[i];
    //         }
    //     }
    // }

    function getpotentialLaunderers() public onlyNGO view returns (address[] memory) {
        if (address(this).balance > maxSave)
            return Donors;

        else
            return empty;
    }

    function getHugeDonors() public onlyNGO view returns (address[] memory, uint[] memory) {
        return (hugeDonors, amountHuge);
    }

    function getDonors() public view returns (address[] memory) {
        return Donors;
    }


}
